    def logowanie(self):
        email = self.lineEdit_email.text()
        password = hashlib.md5(self.lineEdit_password.text().encode('utf-8')).hexdigest()
        db = dbConnection()
        db.connect()
        query = f'SELECT count(*) FROM users WHERE email= %s AND pass= %s'
        params = (email, password)
        row = db.fetchone(query, params)
        db.close()
        if row[0]==1:
            loggedas = f"You are logged as {email}"
            messagebox("Login", loggedas, "Information", "Ok")
        else:
            messagebox("Login problem", "Your email or password is not correct.", "Critical", "Cancel")            
        

